//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//
	bool  gyro_valid = false;
	float gyro_angle;

	task runGyro() {
		int realBias;
		int value;
		float angle;
		float offsetAngle;
		int f = 0;
		bLCDBacklight = false;
		SensorType[gyro] = sensorNone;
		wait1Msec(500);
		SensorType[gyro]= sensorGyro;
		wait1Msec(500);
		realBias = SensorBias[gyro];
		SensorBias[gyro] = SensorBias[gyro] - 1000;

		wait1Msec(1000);

		SensorScale[gyro] = 130;
	  bLCDBacklight = true;
		offsetAngle =  SensorValue[gyro];
		while(true){
			value = SensorValue[gyro];

			// Create float angle, remove offset
			angle = (value - offsetAngle) / 10.0;

			// normalize into the range 0 - 360
			//if( angle < 0 )
			//	angle += 360;

			// put in global for display
			gyro_angle = angle;

			// We can use the angle
			gyro_valid = true;

			// Delay

			wait1Msec(100);
			//writeDebugStreamLine("Angle %f | %i", gyro_angle, f++);
		}
	}
